const https = require(''https'');

// Check if there''s a webhook app in Composio
const options = {
  hostname: ''backend.composio.dev'',
  path: ''/api/v1/apps?category=webhook'',
  method: ''GET'',
  headers: {
    ''x-api-key'': ''ak_t-F0AbvfZHUZSUrqAGNn''
  }
};

const req = https.request(options, (res) => {
  let data = '''';
  res.on(''data'', chunk => data += chunk);
  res.on(''end'', () => {
    const parsed = JSON.parse(data);
    const apps = parsed.items || [];
    const webhookApps = apps.filter(a => 
      a.name.toLowerCase().includes(''webhook'') || 
      a.description?.toLowerCase().includes(''webhook'')
    );
    console.log(''Webhook-related apps:'', webhookApps.map(a => a.name));
  });
});
req.on(''error'', console.error);
req.end();
")

